mapper.select:
select o.ORDER_ID code,k.DEPT_NAME rec_stock, o.SUPPLIER_ID sup_id,s.SUPPLIER_NAME sup_name,o.ORDER_TIME create_time, o.SUBMIT_USER sub_user,o.SUBMIT_TIME sub_time,o.TAKE_ADDRESS rec_addr,o.TAKER rec_user,m.ID msg_id,DECODE(o.ORDER_CLASS,'3','2','1') ord_type
from BILL_PURCHASE_ORDER o JOIN INTERFACE_MSG m on m.DATA_CODE = o.ORDER_ID LEFT JOIN DIC_SUPPLIER s on s.SUPPLIER_ID = o.SUPPLIER_ID LEFT JOIN DIC_DEPT k on k.DEPT_CODE = o.DEPT_CODE
m.status = 0 and m.data_type = 1
and rownum <= #{pageSize}
order by ${cm.orderBy}
${cm.sortAsc}

SELECT t.MATERIAL_CODE code,t.MATERIAL_NAME name,t.SPELL_CODE pinyin,t.MIN_SPEC spec,t.MIN_UNIT unit, t.FACTORY_CODE factory_id,f.FACTORY_NAME factory,t.REGISTRATION_NO reg_cert_code,t.CLASS_CODE,t.PURCHASE_PRICE price, t.MATERIAL_TYPE type,t.ISHIGHT,t.FUNCTION_INFO,t.USE_INFO,t.QUALITY_INFO material,t.STATUS,m.id msg_id FROM DIC_MATERIAL t LEFT JOIN DIC_FACTORY f on f.FACTORY_CODE = t.FACTORY_CODE JOIN INTERFACE_MSG m on m.DATA_CODE = t.MATERIAL_CODE
m.status = 0 and m.data_type = 4
and rownum <= #{pageSize}
